import React from "react";
import { useDispatch, useSelector } from "react-redux";
import { selectCurrentUser, logOut } from "../features/session/sessionSlice";
import { NavLink } from "react-router-dom";
// Import the NavLink component.

export default function Header() {
  const currentUser = useSelector(selectCurrentUser);
  const dispatch = useDispatch();

  const handleLogout = (e) => {
    dispatch(logOut());
  };

  // Replace the 4 <a> tags with <NavLink> components
  return (
    <div className="header">
      <nav href="/about">About</nav>
      <nav href="/articles">Articles</nav>
      <nav href="/categories">Categories</nav>
      {currentUser.username ? (
        <>
          <nav href="/profile">Profile</nav>
          <button onClick={handleLogout} className="logout">
            {" "}
            Log Out{" "}
          </button>
        </>
      ) : (
        <nav href="/sign-up">Sign Up</nav>
      )}
    </div>
  );
}
